window.addEventListener("DOMContentLoaded",()=>{gsap.registerPlugin(ScrollTrigger);const paragraphs=document.querySelectorAll(".text-fade-up");paragraphs.forEach(paragraph=>{const text=paragraph.textContent.trim();const wrappedText=text.split(" ").map(word=>{const letters=word.split("").map(char=>`<span class="char">${char}</span>`).join("");return `<span class="word">${letters}&nbsp;</span>`}).join("");paragraph.innerHTML=wrappedText;gsap.set(paragraph.querySelectorAll(".char"),{opacity:0,y:30});gsap.to(paragraph.querySelectorAll(".char"),{scrollTrigger:{trigger:paragraph,start:"top 85%",end:"top 30%",toggleActions:"play none none reverse",scrub:!0,refreshPriority:1},opacity:1,y:0,ease:"power2.out",duration:0.6,stagger:0.03})})})