(function($){"use strict";gsap.registerPlugin(ScrollTrigger,ScrollSmoother,ScrollToPlugin,SplitText);if(document.querySelector("[data-scroll-container]")){ScrollSmoother.create({wrapper:'[data-scroll-wrapper]',content:'[data-scroll-container]',smooth:1.2,effects:!0,})}
const animateText=()=>{const items=document.querySelectorAll(".title-fade-up");items.forEach(element=>{if(element.classList.contains("is-split"))return;const split=new SplitText(element,{type:"words,chars"});element.classList.add("is-split");gsap.set(element,{perspective:400});gsap.timeline({scrollTrigger:{trigger:element,start:"top 90%",end:"bottom 20%",toggleActions:"play reverse play reverse",scrub:!1,}}).from(split.chars,{opacity:0,rotationX:-80,duration:0.6,stagger:0.05,ease:"power2.out",transformOrigin:"top center -80",})})};window.addEventListener("load",()=>{animateText();ScrollTrigger.refresh()})})(jQuery)